/* 
注册用户展示页
*/ 
import React, { useEffect,useState } from 'react';
import { Button,Popconfirm,Table } from 'antd';
import type { TableColumnsType } from 'antd';
import { useNavigate } from "react-router-dom";
interface DataType {
  key: React.Key;
  nickname: string;
  age: number;
  phone: string;
  gender: string;
  password: string;
}
const columns: TableColumnsType<DataType> = [
  {
    title: '姓名',
    width: 100,
    dataIndex: 'nickname',
    key: 'nickname',
  },
  {
    title: '年龄',
    width: 100,
    dataIndex: 'age',
    key: 'age',
  },
  {
    title: '手机号',
    dataIndex: 'phone',
    key: 'phone',
    width: 150,
  },
  {
    title: '邮箱',
    dataIndex: 'email',
    key: 'email',
    width: 150,
  },
  {
    title: '性别',
    dataIndex: 'gender',
    key: 'gender',
    width: 150,
  },
  {
    title: '操作',
    key: 'operation',
    fixed: 'right',
    width: 100,
      render: (_, record) =>
      (
          <Popconfirm title="Sure to delete?" onConfirm={() => handleDelete(record.id)}>
            <a>删除</a>
          </Popconfirm>
        ) 
  },
];


const SignupTable: React.FC = () => {
    const navigate = useNavigate();
      const [data, setData] = useState< DataType[]>([]);

    // 新增注册
const handleAdd = () => {
    console.log('注册用户');
    navigate("/SignupForm");
    
    }
    useEffect(() => {
    setData(JSON.parse(window.localStorage.getItem('signList')||'') || [])
},[data])
return (
    <>
        <Button onClick={handleAdd} type="primary" style={{ marginBottom: 16 }}>
        注册用户
      </Button>
  <Table columns={columns} dataSource={data} scroll={{ x: 1500, y: 300 }} />

    </>);
} 

export default SignupTable;
